import React from "react";

function Contact(props) {
    return(
        <form class="cmxform form-dislpay" id="contactForm" method="git" action="#">
            <h2>Please provide your name, email address (won't be published) and add a comment with your request or feedback</h2>
            <div class="form-spacing">
                <label for="cname">Full Name (required)</label>
                <input id="cname" name="name" minlength="5" type="text" required placeholder="Full Name"/>
            </div>
            <div class="form-spacing">
                <label for="cemail">email (required)</label>
                <input id="cemail" type="email" name="email" required placeholder="email"/>
        </div>
        <div class="form-spacing">
          <label for="password">Create Password</label>
          <input type="password" id="password" name="password" placeholder="Password" />
        </div>
        <div class="form-spacing">
          <label for="confirm_password">Confirm password</label>
          <input type="password" id="confirm_password" name="confirm_password" placeholder="Confirm password" />
        </div>
        <div class="form-spacing">
          <label for="ccomment">Your comment (required)</label>
          <textarea class="form-comment" id="ccomment" name="comment" required></textarea>
        </div>
        <div class="form-spacing">
          <button type="submit" class="btn btn-primary mb-2">Submit</button>
        </div>
    </form>
    )

    $("#contactForm").validate({
      rules: {
          name: "required",
          password: {
              required: true,
              minlength: 5
          },
          confirm_password: {
              required: true,
              minlength: 5,
              equalTo: "#password"
          },
          email: {
              required: true,
              email: true
          },
      },
          messages: {
          name: "Please enter your name!",
          password: {
              required: "Please provide a password",
              minlength: "Your password must be at least 5 characters long"
          },
          confirm_password: {
              required: "Please provide a password",
              minlength: "Your password must be at least 5 characters long",
              equalTo: "Please enter the same password as above"
          },
          email: "Please enter a valid email address",
          agree: "Please accept our policy"
      },
      errorElement: "em",
      errorPlacement: function ( error, element ) {
          error.addClass( "help-block" );

          if ( element.prop( "type" ) === "checkbox" ) {
              error.insertAfter( element.parent( "label" ) );
          } else {
              error.insertAfter( element );
          }
      },
      highlight: function ( element, errorClass, validClass ) {
          $( element ).parents( "p" ).addClass( "has-error" ).removeClass( "has-success" );
      },
      unhighlight: function (element, errorClass, validClass) {
          $( element ).parents( "p" ).addClass( "has-success" ).removeClass( "has-error" );
  }
  });
}

export default Contact;
