import React from "react";
import Specials from "./components/specials/Specials";
import Product from "./components/product/Product";
import Contact from "./components/contact-info/Contact";
import Footer from "./components/footer/Footer";
import Wrapper from "./components/Wrapper/index";
import products from "./products.json";
import "./App.css";
import Navegation from "./components/navegation/Nav";
import { BrowserRouter as Router, Switch, Route} from 'react-router-dom';
import MainPage from "./components/main-page/MainPage"


class App extends React.Component{
  state = {
     products,
     searchProducts: []
}

filtering(event) {
  const {products} = this.state
  event.preventDefault()
  const filter = products.filter(item => item.id.toLowerCase().search(event.target.value.toLowerCase(),
  ) !== -1);
console.log(filter)
this.setState({searchProducts: filter})

}

  render() {
    console.log(this.state.products)

    return (
    <Wrapper>
      <Router>
      <Navegation filtering={this.filtering}/>
      <Specials/>

        <Switch>
          <Route exact path="/" render={() => <MainPage />} />
          <Route path="/products" render={() => <Product products={this.state.products}/>} />
          <Route path="/contact" render={() => <Contact/>}/>
        </Switch>       
      <Footer/>
      </Router>
    </Wrapper>
  );
}
}


export default App;
