import React from "react";
import Specials from "./components/specials/Specials";
import Product from "./components/product/Product";
import Footer from "./components/footer/Footer";
import Wrapper from "./components/Wrapper/index";
import products from "./products.json";
import "./App.css";
import Navegation from "./components/navegation/Nav";

class App extends React.Component{
  state = {
     products,
     productSearch,

}

  render() {
    return (
    <Wrapper>
      <Navegation/>
      <Specials/>
      {this.state.products.map((item)=> {
        return(
          <Product
          id = {item.id}
          name={item.name}
          image={item.image}
          description={item.description}
          price={item.price}/>
        )
        })}
        <Footer/>
    </Wrapper>
  );
}

productSearch() {
  var input, filter, ul, li, a, i, txtValue;
  input = document.getElementById("prodInput");
  filter = input.value;
  ul = document.getElementById("productGrid");
  li = ul.getElementsByClassName("col-4");
  for (i = 0; i < li.length; i++) {
      a = li[i].getElementsByTagName("h2")[0];
      txtValue = a.textContent || a.innerText;
      if (txtValue.toLowerCase().includes(filter.toLowerCase())) {
          li[i].style.display = "";
      } else {
          li[i].style.display = "none";
      }
  }
}
}


export default App;
