import React from "react";
import "./specials.css";

function Specials(props) {
  return (
    <div>
        {/* <img className="special-img" src="http://www.consumerdepot.com/Otherimages/Media/img/test-1.jpg" alt="deals"></img> */}
        <h2>Search from price range:</h2>
        <div class="checking-itms">
            <input type="checkbox" id="lowerPrice" value="true">less than $300</input>
            <input type="checkbox" id="higherPrice" value="false">less than $500</input>
            <input class="btn btn-primary" type="submit" value="Submit" onclick="filterPrice()"></input>
        </div>
        </div>
  );
}

export default Specials;
