import React from "react";
import Product from "./components/product/Product";
import Contact from "./components/contact-info/Contact";
import Footer from "./components/footer/Footer";
import Wrapper from "./components/Wrapper/index";
import products from "./products.json";
import "./App.css";
import Navegation from "./components/navegation/Nav";
import { BrowserRouter as Router, Switch, Route} from 'react-router-dom';
import MainPage from "./components/main-page/MainPage"


class App extends React.Component{
  state = {
     searchProducts: products
}

filtering = (event) => {
  const nameSearch = products.filter(item => item.name.toLowerCase().search(event.target.value.toLowerCase(),
  ) !== -1);
  if(event.target.value !== ""){
    this.setState({searchProducts: nameSearch})
  } else {
    this.setState({searchProducts: products})
  }
}

dropboxChange = (event) => {
  const filter = products.filter(item => item.type.toLowerCase().search(event.target.value.toLowerCase(),
  ) !== -1);

  if(event.target.value !== "All"){
    this.setState({searchProducts: filter})
  } else {
    this.setState({searchProducts: products})
  }
}

filterPriceLow = (event) => {
  const filPriceLow =  products.filter(function(a){return a.price <= 400});
  this.setState({
    searchProducts: filPriceLow
  })
}

filterPriceHigh = (event) => {
  const filPriceHigh = products.filter(function(a){return a.price >= 400})
  this.setState({
    searchProducts: filPriceHigh
  })
}
showAll = (event) => {
  const showAll = products.filter(function(a){return a.price >= 0})
  this.setState ({
    searchProducts: showAll
  })
}

  render() {
    return (
    <Wrapper>
      <Router>
      <Navegation dropboxChange={this.dropboxChange} filterLow={this.filterPriceLow} filterHigh={this.filterPriceHigh} allProducts={this.showAll}/>
        <Switch>
          <Route exact path="/" render={() => <MainPage />} />
          <Route path="/products" render={() => <Product products={this.state.searchProducts}/>} />
          <Route path="/contact" render={() => <Contact/>}/>
        </Switch>       
      <Footer/>
      </Router>
    </Wrapper>
  );
}
}


export default App;
